package com.ketty.mybatis.chainOfResponsibility;import com.google.common.collect.Lists;import java.util.List;/** * @Author ketty bluce * @Create 2023/7/25 * @Version 1.0 */interface Interceptor {    Object plugin(Object target);}class InterceptorA implements Interceptor {    @Override    public Object plugin(Object target) {        System.out.println("InterceptorA");        return target;    }}class InterceptorB implements Interceptor {    @Override    public Object plugin(Object target) {        System.out.println("InterceptorB");        return target;    }}class InterceptorC implements Interceptor {    @Override    public Object plugin(Object target) {        System.out.println("InterceptorC");        return target;    }}class InterceptorChain {    private List<Interceptor> interceptorList = Lists.newArrayList();    public void addInterceptor(Interceptor interceptor) {        interceptorList.add(interceptor);    }    public Object pluginAll(Object target) {        for (Interceptor interceptor : interceptorList) {            target = interceptor.plugin(target);        }        return target;    }}public class InterceptorDemo {    public static void main(String[] args) {        InterceptorA interceptorA=new InterceptorA();        InterceptorB interceptorB=new InterceptorB();        InterceptorC interceptorC=new InterceptorC();        InterceptorChain interceptorChain=new InterceptorChain();        interceptorChain.addInterceptor(interceptorA);        interceptorChain.addInterceptor(interceptorB);        interceptorChain.addInterceptor(interceptorC);    }}